package com.china.demo.shardingjdbchint.common;import io.shardingsphere.api.algorithm.sharding.ListShardingValue;import io.shardingsphere.api.algorithm.sharding.ShardingValue;import io.shardingsphere.api.algorithm.sharding.hint.HintShardingAlgorithm;import java.util.ArrayList;import java.util.Collection;import java.util.List;/** * OrderShardingAlgorithm * * @author shanmin.zhang * @date 19/4/23 **/public class OrderShardingAlgorithm implements HintShardingAlgorithm {    @Override    public Collection<String> doSharding(Collection<String> collection, ShardingValue shardingValue) {        ListShardingValue<Integer> listShardingValue = (ListShardingValue<Integer>) shardingValue;        List<String> shardingResults = new ArrayList<>();        collection.forEach(targetName -> {            String suffix = targetName.substring(targetName.lastIndexOf("-") + 1);            listShardingValue.getValues().forEach(value -> {                if (value % 2 == Integer.parseInt(suffix)) {                    shardingResults.add(targetName);                }            });        });        return shardingResults;    }}