package com.compent;import lombok.extern.slf4j.Slf4j;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.stereotype.Component;/** * KafkaListenerTest 一个topic广播到多个customer Group * * @author shanmin.zhang * @date 19/1/21 **/@Component@Slf4jpublic class KafkaListenerGroupTest {    @KafkaListener(topics = "mask-word-topic", groupId = "${appconfig.customerTopic}")    public void listen(String value) {        System.out.println("-----========》广播组1 " + value);        log.info("-->" + value);    }}