package com.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.kafka.core.KafkaTemplate;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * KafkaController * * @author shanmin.zhang * @date 19/1/21 **/@RestControllerpublic class KafkaController {    @Autowired    private KafkaTemplate<String, String> kafkaTemplate;    @RequestMapping("/send")    public String send(String name) {        kafkaTemplate.send("mask-word-topic", "change");        return name;    }}