package com.controller;import com.entity.FormRequest;import com.entity.TicketUpdateForm;import com.validator.GroupVaild;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;/** * ValidatedTestController * * @author shanmin.zhang * @date 19/4/29 **/@RestControllerpublic class ValidatedTest2Controller {    @PostMapping("/form")    public FormRequest form(@Valid FormRequest form) {        return form;    }    @PutMapping("/ticket/create")    public TicketUpdateForm crate(@Validated(value = GroupVaild.CreateGroup.class) TicketUpdateForm form) {        return form;    }    @PutMapping("/ticket/update")    public TicketUpdateForm update(@Validated(value = GroupVaild.UpdateGroup.class) TicketUpdateForm form) {        return form;    }}