package com.controller;import com.entity.FormRequest;import com.entity.RegisterForm;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;import javax.validation.constraints.Email;/** * ValidatedTestController * * @author shanmin.zhang * @date 19/4/29 **/@Validated@RestController@RequestMapping("/valid")public class ValidatedTestController {    @GetMapping("/path/{group:[a-zA-Z0-9_]+}/{userId}")    public String path(@PathVariable("group") String group, @PathVariable("userId") Integer userId) {        return group + ":" + userId;    }    @GetMapping("/param")    public String param(@RequestParam("group") @Email String group,                        @RequestParam("userid") Integer userid) {        return group + ":" + userid;    }    @PostMapping("/form1")    @ResponseBody    public FormRequest form(FormRequest form) {        return form;    }    @PostMapping("/reg")    @ResponseBody    public RegisterForm reg(RegisterForm form) {        return form;    }}