package com.controller.api;import com.alibaba.fastjson.JSONObject;import com.entity.UserInfo;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * UserApiController * * @author shanmin.zhang * @date 2021/11/4 **/@Api("用户管理api")@RestController@RequestMapping("/userApi")public class UserApiController {    @ApiOperation("查询用户信息")    @PostMapping("/findUserList")    public JSONObject findUserList() {        JSONObject jsonObject = new JSONObject();        jsonObject.put("success", true);        jsonObject.put("result", "true");        return jsonObject;    }    @ApiOperation("保存用户")    @PostMapping("saveUser")    public JSONObject saveUser(@RequestBody UserInfo userInfo) {        JSONObject jsonObject = new JSONObject();        jsonObject.put("success", true);        jsonObject.put("message", null);        return jsonObject;    }}