package com.entity;import com.annotation.IsMobile;import lombok.Data;import javax.validation.constraints.*;@Datapublic class FormRequest {    @NotEmpty    @Email    private String email;    @Pattern(regexp = "[a-zA-Z0-9_]{6,30}",message = "格式不对")    @NotEmpty    private String name;    @Min(5)    @Max(199)    private int age;    @IsMobile    private String phone;}