package com.entity;import com.annotation.PasswordEquals;import lombok.Data;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.NotEmpty;/** * RegisterForm * * @author shanmin.zhang * @date 19/4/29 **/@Data@PasswordEqualspublic class RegisterForm {    @NotEmpty    @Length(min = 5, max = 30)    private String username;    @NotEmpty    private String password;    @NotEmpty    private String passwordConfirm;}