package com.denachina.webflux.function;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.reactive.function.server.*;/** * RouterConfig * * @author shanmin.zhang * @date 18/12/6 **/@Configurationpublic class RouterConfig {    @Autowired    private TimeHandler timeHandler;    @Bean    public RouterFunction<ServerResponse> timeRouter() {        return RouterFunctions.route(RequestPredicates.GET("/time"), timeHandler::getTime)                .andRoute(RequestPredicates.GET("/date"), timeHandler::getDate)                .andRoute(RequestPredicates.GET("/times"), timeHandler::sendTimePerSec);    }}