package com.denachina.webflux.pojo;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;/** * MsgEvent * * @author shanmin.zhang * @date 18/12/7 **/@Data@Document(collection = "msg_event")public class MsgEvent {    @Id    private String  id;    @Indexed(name = "idx_msg")    private String username;    private String message;    private MsgEvent(Builder builder) {        this.username = builder.username;        this.message = builder.message;    }    public static class Builder {        private String username;        private String message;        public Builder username(String username) {            this.username = username;            return this;        }        public Builder message(String message) {            this.message = message;            return this;        }        public MsgEvent build() {            return new MsgEvent(this);        }    }}