package com.denachina.webflux.pojo;import com.alibaba.fastjson.JSONObject;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;import java.util.Date;/** * User * MongoDB是文档型的NoSQL数据库，因此，我们使用@Document注解User类 * MongoDB会自动创建collection，默认为类名首字母小写，也就是user。 * * @author shanmin.zhang * @date 18/12/6 **/@Data@AllArgsConstructor@NoArgsConstructor@Documentpublic class User {    /**     * 注解属性id为ID     */    @Id    private String id;    @Indexed(unique = true)    private String username;    private String phone;    private String email;    private String name;    private Date birthday;    @Override    public String toString() {        return JSONObject.toJSONString(this);    }}